{% block right_title %}Les catégories de produits{% endblock %}
{% block right_content %}
\t
\t{% for category in categories %}
\t<div class=\"col-sm-4\">
\t\t<div class=\"product-image-wrapper\">
\t\t\t<div class=\"single-products\">
\t\t\t\t<div class=\"productinfo text-center\">
\t\t\t\t\t<img src=\"/uploads/categories/{{ category.image }}\" height=\"160px\" />
\t\t\t\t\t<a href=\"{{path('eshop_category', {'id': category.id})}}\" class=\"btn btn-default add-to-cart\">{{category.label}}</a>
\t\t\t\t</div>
\t\t\t</div>
\t\t</div>
\t</div>
\t{% endfor %}


{% endblock %}

